import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [readMore, setReadMore] = useState(false);
  const [categories, setCategories] = useState([]); // New state for categories
  const [selectedCategory, setSelectedCategory] = useState(""); // New state for selected category
  const descriptionLimit = 100;
  const token = localStorage.getItem("token");

  const navigate = useNavigate();

  const toggleReadMore = () => {
    setReadMore(!readMore);
  };

  const addToCart = async (product) => {
    let user = localStorage.getItem("user");
    try {
      const userData = localStorage.getItem("user");
      if (userData) {
        user = userData;
      } else {
        alert("Please log in to add items to the cart");
        return;
      }
    } catch (error) {
      alert("There was an issue retrieving your user data. Please log in again.");
      return;
    }

    const userId = user;
    let cart = JSON.parse(localStorage.getItem("cart")) || [];
    const existingProduct = cart.find((item) => item._id === product._id);

    if (existingProduct) {
      existingProduct.quantity += 1;
    } else {
      cart.push({ ...product, quantity: 1 });
    }

    localStorage.setItem("cart", JSON.stringify(cart));

    try {
      const response = await axios.post(
        "http://localhost:4000/api/addToCart",
        {
          productId: product._id,
          quantity: 1,
          userId: user,
        }
      );
      alert("Product added to cart successfully");
      navigate("/cart");
    } catch (error) {
      alert("Failed to add product to cart");
    }
  };

  const handleView = async (product) => {
    const productId = product._id;
    const userId = localStorage.getItem("user");

    try {
      const response = await axios.post(
        `http://localhost:4000/api/productView/${productId}`,
        {
          userId: userId,
        }
      );
      navigate(`/product/${productId}`);
    } catch (error) {
      alert("Failed to view product");
    }
  };

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
  };

  useEffect(() => {
    // Fetch categories
    axios
      .get("http://localhost:4000/api/categories")
      .then((response) => {
        setCategories(response.data);
      })
      .catch((error) => {
        setError(error.message);
      });

    // Fetch products
    axios
      .get("http://localhost:4000/api/productList")
      .then((response) => {
        setProducts(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  const filteredProducts = selectedCategory
    ? products.filter((product) => product.category === selectedCategory)
    : products;

  if (loading) {
    return <div className="text-center mt-10">Loading...</div>;
  }

  if (error) {
    return <div className="text-center text-red-500">Error: {error}</div>;
  }

  if (products.length === 0) {
    return <div className="text-center mt-10">No products found.</div>;
  }

  return (
    <div className="p-5 md:p-20">
      {/* Category Filter */}
      <div className="mb-4">
        <label htmlFor="category" className="text-lg font-bold mr-2">
          Filter by Category:
        </label>
        <select
          id="category"
          value={selectedCategory}
          onChange={handleCategoryChange}
          className="p-2 border rounded"
        >
          <option value="">All Categories</option>
          {categories.map((category) => (
            <option key={category._id} value={category.name}>
              {category.name}
            </option>
          ))}
        </select>
      </div>

      <div className="flex flex-wrap justify-center">
        {filteredProducts.map((product) => (
          product && product.name ? (
            <div
              className="bg-white shadow-md rounded-lg overflow-hidden m-4 w-72"
              key={product._id}
            >
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-48 object-cover"
              />
              <div className="p-4">
                <h2 className="text-lg font-bold mb-2">{product.name}</h2>
                <p className="text-sm font-medium text-gray-600 mb-2">
                  Artisan: {product.artisanId ? product.artisanId.name : "Unknown"}
                </p>
                <p className="text-sm text-gray-700 mb-2">
                  {readMore
                    ? product.description
                    : product.description.slice(0, descriptionLimit) +
                      (product.description.length > descriptionLimit
                        ? "..."
                        : "")}
                  {product.description.length > descriptionLimit && (
                    <span
                      onClick={toggleReadMore}
                      className="text-blue-500 cursor-pointer font-bold"
                    >
                      {readMore ? " Read Less" : " Read More"}
                    </span>
                  )}
                </p>
                <p className="text-lg font-bold text-black mb-4">
                  ₹{product.price}
                </p>
                <button
                  className="w-full bg-blue-500 text-white py-2 px-4 rounded mb-2 hover:bg-blue-600"
                  onClick={() => addToCart(product)}
                >
                  Add to Cart
                </button>
                <button
                  className="w-full bg-gray-700 text-white py-2 px-4 rounded hover:bg-gray-800"
                  onClick={() => handleView(product)}
                >
                  View Product
                </button>
              </div>
            </div>
          ) : (
            <div key={product._id}>Product data is missing</div>
          )
        ))}
      </div>
    </div>
  );
};

export default ProductsList;
