import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
// import '../styles/PaymentPage.css';

const PaymentPage = () => {
  const { state } = useLocation();
  const { cart = [], totalPrice = 0 } = state || {}; 
  const [paymentStatus, setPaymentStatus] = useState(null);
  const artistId = localStorage.getItem("artisanIdproduct");
  const userId = localStorage.getItem("user");
  const [allowCOD, setAllowCOD] = useState(false); 
  const [user, setUser] = useState(null); 
  const [codCount, setCodCount] = useState(0); 
  const [address, setAddress] = useState(""); 
  const [isAddressValid, setIsAddressValid] = useState(false); 
  const [cardDetails, setCardDetails] = useState({
    cardNumber: "",
    expiryDate: "",
    cvv: ""
  });
  c

  // Check user's COD limit on component mount
  useEffect(() => {
    const checkCODLimit = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/api/cod/${userId}`);
        setUser(response.data);
        setCodCount(response.data.codCount);
        if (response.data.codCount >= 5) {
          setAllowCOD(true);
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };
    checkCODLimit();
  }, [userId]);

  // Handle payment for both COD and Online
  const handlePayment = async (paymentMethod) => {
    if (!isAddressValid) {
      alert("Please enter a valid address before making the payment.");
      return;
    }

    if (!artistId) {
      setPaymentStatus("Error: Artisan not found.");
      return;
    }

    // if (paymentMethod === "COD" && codCount >= 5) {
    //   setPaymentStatus("COD limit reached. Please use online payment.");
    //   return;
    // }

    if (paymentMethod === "Online") {
      const { cardNumber, expiryDate, cvv } = cardDetails;
      if (!/^\d{12,19}$/.test(cardNumber) || !/^\d{2}\/\d{2}$/.test(expiryDate) || !/^\d{3}$/.test(cvv)) {
        setPaymentStatus("Invalid card details. Please check and try again.");
        return;
      }
    }

    setPaymentStatus("Processing...");

    const productData = cart.map(item => ({
      productId: item._id, 
      quantity: item.quantity,
      pname: item.name,
    }));

    try {
      const [updateResponse, codResponse, cartResponse] = await Promise.allSettled([
        axios.post("http://localhost:4000/api/updateearned", { artisianId: artistId, amount: totalPrice }),
        axios.post(`http://localhost:4000/api/checkcod/${userId}`),
        axios.post("http://localhost:4000/api/usercart", { userId, quantity: cart.length, productIds: productData, address })
      ]);

      if (updateResponse.status === 'fulfilled' && cartResponse.status === 'fulfilled') {
        setPaymentStatus("Payment Successful!");
        window.location.href='/your-profile';
      } else {
        setPaymentStatus("Payment Failed. Please try again.");
      }
    } catch (error) {
      console.error("Payment error:", error);
      setPaymentStatus("Payment Failed. Please try again.");
    }
  };

  // Handle user address input
  const handleAddressChange = (e) => {
    const inputAddress = e.target.value;
    setAddress(inputAddress);
    const isValidAddress = /^[a-zA-Z0-9\s,'-]{5,}$/.test(inputAddress); // Improved address validation
    setIsAddressValid(isValidAddress);
  };

  // Handle card details input
  const handleCardInputChange = (e) => {
    const { id, value } = e.target;
    setCardDetails(prevDetails => ({
      ...prevDetails,
      [id]: value
    }));
  };

  return (
    <div className="payment-container">
      <h1 className="payment-heading">Payment Details</h1>

      <div className="cart-details">
        <h2>Cart Items</h2>
        {cart.length === 0 ? (
          <p>No items in the cart</p>
        ) : (
          cart.map((item) => (
            <div key={item._id} className="cart-item">
              <img src={item.image} alt={item.name} className="cart-image" />
              <div className="cart-item-info">
                <p>{item.name}</p>
                <p>₹{item.price}</p>
                <p>Quantity: {item.quantity}</p>
                <p>Total: ₹{item.price * item.quantity}</p>
              </div>
            </div>
          ))
        )}
        <div className="cart-summary">
          <h3>Total: ₹{totalPrice}</h3>
        </div>
      </div>

      <div className="address-section">
        <h2>Enter Your Address</h2>
        <textarea
          placeholder="Enter your delivery address"
          value={address}
          onChange={handleAddressChange}
          className="address-input"
        />
        {!isAddressValid && <p className="error-message">Please enter a valid address with at least 5 characters.</p>}
      </div>

      <div className="payment-options">
        <h2>Payment Method</h2>
        <h3>{codCount < 5 ? `${5 - codCount} COD orders left before online payment is available.` : 'You can now use both COD and online payment options.'}</h3>
        
        <button
          onClick={() => handlePayment("COD")}
          className="payment-button"
        >
          Cash on Delivery
        </button>

        {codCount >= 5 && (
          <>
            <form>
              <label htmlFor="cardNumber">Card Number:</label>
              <input 
                type="number" 
                placeholder="1234567890123456" 
                id="cardNumber" 
                value={cardDetails.cardNumber}
                onChange={handleCardInputChange}
              />

              <label htmlFor="expiryDate">Expiry Date (MM/YY):</label>
              <input 
                type="text" 
                placeholder="MM/YY" 
                id="expiryDate" 
                value={cardDetails.expiryDate}
                onChange={handleCardInputChange}
              />

              <label htmlFor="cvv">CVV:</label>
              <input 
                type="number" 
                placeholder="000" 
                id="cvv" 
                value={cardDetails.cvv}
                onChange={handleCardInputChange}
              />
            </form>

            <button
              onClick={() => handlePayment("Online")}
              className={isAddressValid ? "payment-button" : "payment-button disabled-button"}
              disabled={!isAddressValid}
            >
              Pay Online
            </button>
          </>
        )}
      </div>

      {paymentStatus && <p>{paymentStatus}</p>}
    </div>
  );
};

export default PaymentPage;
