import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const SuperAdmin = () => {
  const location = useLocation(); // Track the current route

  const [leader, setLeader] = useState([]);
  const [artisans, setArtisans] = useState([]);
  const [users, setUsers] = useState([]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const leaderboardResponse = await axios.get('http://localhost:4000/api/leaderboard');
        const sortedLeaderboard = leaderboardResponse.data.sort((a, b) => b.totalSales - a.totalSales);
        setLeader(sortedLeaderboard);

        const artisansResponse = await axios.get('http://localhost:4000/api/artists');
        setArtisans(artisansResponse.data);

        const usersResponse = await axios.get('http://localhost:4000/api/users');
        setUsers(usersResponse.data);

        const productsResponse = await axios.get('http://localhost:4000/api/productList');
        setProducts(productsResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const awardArtisan = async (artisanId) => {
    try {
      const response = await axios.post('http://localhost:4000/api/award', { artisanId });
      console.log(response.data.message);
      alert('Awarded artisan of the month');
    } catch (error) {
      console.error('Error awarding artisan:', error);
    }
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-1/4 bg-gray-800 text-white p-5">
        <h2 className="text-2xl font-bold mb-6">Super Admin Dashboard</h2>
        <ul className="space-y-3">
          <li className="p-3 text-center bg-gray-700 rounded-lg hover:bg-gray-600 cursor-pointer">
            Dashboard
          </li>
        </ul>
      </div>

      {/* Main Content Area */}
      <div className="flex-1 p-5 overflow-y-auto bg-gray-100">
        {/* Section 1: Leaderboard */}
        <div className="mb-8 bg-white shadow-lg rounded-lg p-5">
          <h3 className="text-xl font-bold mb-4">Leaderboard - Artisan of the Month</h3>
          {leader.map((data, index) => (
            <div
              key={index}
              className="bg-gray-50 p-4 rounded-lg mb-4 shadow-md hover:shadow-lg transition"
            >
              <p>
                {index + 1}. {data.name}
              </p>
              <p>Total Sales: ₹{data.earnedAmount}</p>
              {index === 0 && (
                <button
                  className="mt-2 bg-pink-500 hover:bg-pink-600 text-white py-2 px-4 rounded"
                  onClick={() => awardArtisan(data._id)}
                >
                  Award Artisan
                </button>
              )}
            </div>
          ))}
        </div>

        {/* Section 2: All Artisans */}
        <div className="mb-8 bg-white shadow-lg rounded-lg p-5">
          <h3 className="text-xl font-bold mb-4">All Artisans</h3>
          {artisans.map((artisan, index) => (
            <div
              key={index}
              className="bg-gray-50 p-4 rounded-lg mb-4 shadow-md hover:shadow-lg transition"
            >
              <p>
                <strong>Name:</strong> {artisan.name}
              </p>
              <p>
                <strong>Email:</strong> {artisan.email}
              </p>
              <p>
                <strong>Location:</strong> {artisan.location}
              </p>
              <button
                className="mt-2 bg-pink-500 hover:bg-pink-600 text-white py-2 px-4 rounded"
                onClick={() => awardArtisan(artisan._id)}
              >
                Award
              </button>
            </div>
          ))}
        </div>

        {/* Section 3: All Users */}
        <div className="mb-8 bg-white shadow-lg rounded-lg p-5">
          <h3 className="text-xl font-bold mb-4">All Users</h3>
          {users.map((user, index) => (
            <div
              key={index}
              className="bg-gray-50 p-4 rounded-lg mb-4 shadow-md hover:shadow-lg transition"
            >
              <p>
                <strong>Name:</strong> {user.name}
              </p>
              <p>
                <strong>Email:</strong> {user.email}
              </p>
              <p>
                <strong>Role:</strong> {user.role}
              </p>
              <button className="mt-2 bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded">
                Edit
              </button>
              <button className="mt-2 bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded ml-2">
                Delete
              </button>
            </div>
          ))}
        </div>

        {/* Section 4: All Products */}
        <div className="bg-white shadow-lg rounded-lg p-5">
          <h3 className="text-xl font-bold mb-4">All Products</h3>
          {products.map((product, index) => (
            <div
              key={index}
              className="bg-gray-50 p-4 rounded-lg mb-4 shadow-md hover:shadow-lg transition"
            >
              <p>
                <strong>Product Name:</strong> {product.name}
              </p>
              <p>
                <strong>Price:</strong> ₹{product.price}
              </p>
              <p>
                <strong>Category:</strong> {product.category}
              </p>
              <p>
                <strong>Stock:</strong> {product.stock}
              </p>
              <button className="mt-2 bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded">
                Edit
              </button>
              <button className="mt-2 bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded ml-2">
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default SuperAdmin;
