import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import '../../styles/AdminDashboard.css'; // Import the external CSS file

const SuperAdmin = () => {
  const location = useLocation(); // Track the current route

  // States for artisans, users, and products
  const [leader, setLeader] = useState([]);
  const [artisans, setArtisans] = useState([]);
  const [users, setUsers] = useState([]);
  const [products, setProducts] = useState([]);

  // Fetch leaderboard, artisans, users, and products
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch the leaderboard data
        const leaderboardResponse = await axios.get('http://localhost:4000/api/leaderboard');
        const sortedLeaderboard = leaderboardResponse.data.sort((a, b) => b.totalSales - a.totalSales);
        setLeader(sortedLeaderboard); // Update state with sorted leaderboard data

        // Fetch all artisans
        const artisansResponse = await axios.get('http://localhost:4000/api/artists');
        setArtisans(artisansResponse.data); // Update state with artisan data

        // Fetch all users
        const usersResponse = await axios.get('http://localhost:4000/api/users');
        setUsers(usersResponse.data); // Update state with user data

        // Fetch all products
        const productsResponse = await axios.get('http://localhost:4000/api/productList');
        setProducts(productsResponse.data); // Update state with product data

      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);
  // Function to handle awarding the artisan
  const awardArtisan = async (artisanId) => {
    try {
      const response = await axios.post('http://localhost:4000/api/award', { artisanId });
      console.log(response.data.message);
      alert("Awarded artisan of the month");
    } catch (error) {
      console.error('Error awarding artisan:', error);
    }
  };

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Sidebar */}
      <div className="sidebar">
        <h2 className="title">Super Admin Dashboard</h2>
        <ul className="menu">
          <li>Dashboard</li>
        </ul>
      </div>

      {/* Main Content Area */}
      <div className="main-content">
        
        {/* Section 1: Leaderboard */}
        <div className="section">
          <h3>Leaderboard - Artisan of the Month</h3>
          {leader.map((data, index) => (
            <div key={index} className="card">
              <p>{index + 1}. {data.name}</p>
              <p>Total Sales: ₹{data.earnedAmount}</p>
              {index === 0 && <button onClick={() => awardArtisan(data._id)}>Award Artisan</button>}
            </div>
          ))}
        </div>

        {/* Section 2: All Artisans */}
        <div className="section">
          <h3>All Artisans</h3>
          {artisans.map((artisan, index) => (
            <div key={index} className="card">
              <p><strong>Name:</strong> {artisan.name}</p>
              <p><strong>Email:</strong> {artisan.email}</p>
              <p><strong>Location:</strong> {artisan.location}</p>
              <button onClick={() => awardArtisan(artisan._id)}>Award</button>
            </div>
          ))}
        </div>

        {/* Section 3: All Users */}
        <div className="section">
          <h3>All Users</h3>
          {users.map((user, index) => (
            <div key={index} className="card">
              <p><strong>Name:</strong> {user.name}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Role:</strong> {user.role}</p>
              <button>Edit</button>
              <button>Delete</button>
            </div>
          ))}
        </div>

        {/* Section 4: All Products */}
        <div className="section">
          <h3>All Products</h3>
          {products.map((product, index) => (
            <div key={index} className="card">
              <p><strong>Product Name:</strong> {product.name}</p>
              <p><strong>Price:</strong> ₹{product.price}</p>
              <p><strong>Category:</strong> {product.category}</p>
              <p><strong>Stock:</strong> {product.stock}</p>
              <button>Edit</button>
              <button>Delete</button>
            </div>
          ))}
        </div>

      </div>
    </div>
  );
};

export default SuperAdmin;
