import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

const PaymentPage = () => {
  const { state } = useLocation();
  const { cart = [], totalPrice = 0 } = state || {}; 
  const [paymentStatus, setPaymentStatus] = useState(null);
  const artistId = localStorage.getItem("artisanIdproduct");
  const userId = localStorage.getItem("user");
  const [allowCOD, setAllowCOD] = useState(false); 
  const [user, setUser] = useState(null); 
  const [codCount, setCodCount] = useState(0); 
  const [address, setAddress] = useState(""); 
  const [isAddressValid, setIsAddressValid] = useState(false); 
  const [cardDetails, setCardDetails] = useState({
    cardNumber: "",
    expiryDate: "",
    cvv: ""
  });
  console.log(cart);
  useEffect(() => {
    const checkCODLimit = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/api/cod/${userId}`);
        setUser(response.data);
        setCodCount(response.data.codCount);
        if (response.data.codCount >= 5) {
          setAllowCOD(true);
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };
    checkCODLimit();
  }, [userId]);

  const handlePayment = async (paymentMethod) => {
    if (!isAddressValid) {
      alert("Please enter a valid address before making the payment.");
      return;
    }

    if (!artistId) {
      setPaymentStatus("Error: Artisan not found.");
      return;
    }

    if (paymentMethod === "Online") {
      const { cardNumber, expiryDate, cvv } = cardDetails;
      if (!/^\d{12,19}$/.test(cardNumber) || !/^\d{2}\/\d{2}$/.test(expiryDate) || !/^\d{3}$/.test(cvv)) {
        setPaymentStatus("Invalid card details. Please check and try again.");
        return;
      }
    }

    setPaymentStatus("Processing...");

    const productData = cart.map(item => ({
      productId: item._id, 
      quantity: item.quantity,
      pname: item.name,
    }));

    try {
      const [updateResponse, codResponse, cartResponse] = await Promise.allSettled([
        axios.post("http://localhost:4000/api/updateearned", { artisianId: artistId, amount: totalPrice }),
        axios.post(`http://localhost:4000/api/checkcod/${userId}`),
        axios.post("http://localhost:4000/api/usercart", { userId, quantity: cart.length, productIds: productData, address })
      ]);

      if (updateResponse.status === 'fulfilled' && cartResponse.status === 'fulfilled') {
        setPaymentStatus("Payment Successful!");
        window.location.href='/your-profile';
      } else {
        setPaymentStatus("Payment Failed. Please try again.");
      }
    } catch (error) {
      console.error("Payment error:", error);
      setPaymentStatus("Payment Failed. Please try again.");
    }
  };

  const handleAddressChange = (e) => {
    const inputAddress = e.target.value;
    setAddress(inputAddress);
    const isValidAddress = /^[a-zA-Z0-9\s,'-]{5,}$/.test(inputAddress); 
    setIsAddressValid(isValidAddress);
  };

  const handleCardInputChange = (e) => {
    const { id, value } = e.target;
    setCardDetails(prevDetails => ({
      ...prevDetails,
      [id]: value
    }));
  };

  return (
    <div className="p-10 bg-gradient-to-r from-white to-gray-200 rounded-lg max-w-2xl mx-auto mt-12 shadow-xl font-sans">
      <h1 className="text-4xl font-bold text-center text-gray-800 mb-10 uppercase tracking-wider">Payment Details</h1>

      <div className="flex flex-col gap-5 mb-8">
        <h2 className="text-2xl font-semibold text-gray-800">Cart Items</h2>
        {cart.length === 0 ? (
          <p>No items in the cart</p>
        ) : (
          cart.map((item) => (
            <div key={item._id} className="flex items-center p-5 bg-white rounded-lg shadow-md hover:transform hover:translate-y-2 hover:shadow-lg transition">
              <img src={item.image} alt={item.name} className="w-24 h-24 object-cover rounded-lg mr-5" />
              <div className="flex flex-col gap-2">
                <p className="text-lg font-semibold text-gray-700">{item.name}</p>
                <p className="text-lg text-gray-600">₹{item.price}</p>
                <p className="text-md text-gray-500">Quantity: {item.quantity}</p>
                <p className="text-lg text-red-600 font-semibold">Total: ₹{item.price * item.quantity}</p>
              </div>
            </div>
          ))
        )}
        <div className="mt-4 text-right text-xl font-semibold text-gray-800">
          <h3>Total: ₹{totalPrice}</h3>
        </div>
      </div>

      <div className="mb-6">
        <h2 className="text-2xl font-semibold text-gray-800">Enter Your Address</h2>
        <textarea
          placeholder="Enter your delivery address"
          value={address}
          onChange={handleAddressChange}
          className="w-full p-3 mt-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        {!isAddressValid && <p className="text-red-500 text-sm mt-2">Please enter a valid address with at least 5 characters.</p>}
      </div>

      <div className="flex flex-col gap-6">
        <h2 className="text-2xl font-semibold text-gray-800">Payment Method</h2>
        <h3 className="text-lg text-gray-700">{codCount < 5 ? `${5 - codCount} COD orders left before online payment is available.` : 'You can now use both COD and online payment options.'}</h3>
        
        <button
          onClick={() => handlePayment("COD")}
          className="bg-gradient-to-r from-purple-600 to-blue-500 text-white py-3 rounded-lg text-lg font-semibold hover:scale-105 transition"
        >
          Cash on Delivery
        </button>

        {codCount >= 5 && (
          <>
            <form className="space-y-4">
              <label htmlFor="cardNumber" className="block text-gray-700">Card Number:</label>
              <input 
                type="number" 
                placeholder="1234567890123456" 
                id="cardNumber" 
                value={cardDetails.cardNumber}
                onChange={handleCardInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
              />

              <label htmlFor="expiryDate" className="block text-gray-700">Expiry Date (MM/YY):</label>
              <input 
                type="text" 
                placeholder="MM/YY" 
                id="expiryDate" 
                value={cardDetails.expiryDate}
                onChange={handleCardInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
              />

              <label htmlFor="cvv" className="block text-gray-700">CVV:</label>
              <input 
                type="number" 
                placeholder="000" 
                id="cvv" 
                value={cardDetails.cvv}
                onChange={handleCardInputChange}
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </form>

            <button
              onClick={() => handlePayment("Online")}
              className={`${
                isAddressValid ? "bg-gradient-to-r from-purple-600 to-blue-500" : "bg-gray-400 cursor-not-allowed"
              } text-white py-3 rounded-lg text-lg font-semibold hover:scale-105 transition`}
              disabled={!isAddressValid}
            >
              Pay Online
            </button>
          </>
        )}
      </div>

      {paymentStatus && <p className="text-xl text-center mt-6 font-semibold text-green-600">{paymentStatus}</p>}
    </div>
  );
};

export default PaymentPage;
