import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [category, setCategory] = useState(""); // Added category state
  const [filteredProducts, setFilteredProducts] = useState([]); // Store filtered products
  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
  };

  const filterProducts = (category) => {
    if (!category) return products;
    return products.filter((product) => product.category === category);
  };

  useEffect(() => {
    axios
      .get("http://localhost:4000/api/productList")
      .then((response) => {
        setProducts(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    setFilteredProducts(filterProducts(category)); // Filter products when category changes
  }, [category, products]);

  if (loading) {
    return <div className="text-center mt-10">Loading...</div>;
  }

  if (error) {
    return <div className="text-center text-red-500">Error: {error}</div>;
  }

  if (products.length === 0) {
    return <div className="text-center mt-10">No products found.</div>;
  }

  return (
    <div>
      <div>
        <select
          value={category}
          onChange={handleCategoryChange}
          className="mb-4"
        >
          <option value="">Select a category</option>
          <option value="Electronics">Electronics</option>
          <option value="Fashion">Fashion</option>
          <option value="Home & Kitchen">Home & Kitchen</option>
          <option value="Beauty & Health">Beauty & Health</option>
          <option value="Sports & Outdoors">Sports & Outdoors</option>
          <option value="Other">Other</option>
        </select>
      </div>

      <div className="flex flex-wrap justify-center p-5 md:p-20">
        {filteredProducts.map((product) => (
          <div
            className="bg-white shadow-md rounded-lg overflow-hidden m-4 w-72"
            key={product._id}
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h2 className="text-lg font-bold mb-2">{product.name}</h2>
              <p className="text-sm font-medium text-gray-600 mb-2">
                Artisan: {product.artisanId ? product.artisanId.name : "Unknown"}
              </p>
              <p className="text-sm text-gray-700 mb-2">
                {product.description}
              </p>
              <p className="text-lg font-bold text-black mb-4">
                ₹{product.price}
              </p>
              <button
                className="w-full bg-blue-500 text-white py-2 px-4 rounded mb-2 hover:bg-blue-600"
                onClick={() => handleAddToCart(product)}
              >
                Add to Cart
              </button>
              <button
                className="w-full bg-gray-700 text-white py-2 px-4 rounded hover:bg-gray-800"
                onClick={() => handleViewProduct(product)}
              >
                View Product
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsList;
